function colorChange() {
    // orange to pink
    var x = document.querySelectorAll("#orange0, #orange1, #orange2, #orange3, #orange4");
        x[0].style.backgroundColor = "#ed559c";
        x[1].style.backgroundColor = "#ed559c";
        x[2].style.backgroundColor = "#ed559c";
        x[3].style.backgroundColor = "#ed559c";

    // blue to hot pink
    var y = document.querySelectorAll("#blue0, #blue1, #blue2, #blue3");
        y[0].style.backgroundColor = "#E2416F";
        y[1].style.backgroundColor = "#E2416F";
        y[2].style.backgroundColor = "#E2416F";

}

function colorChange2() {

     // dark blue to blue
    var z = document.querySelectorAll("#darkblue0, #darkblue1, #darkblue2");
        z[0].style.backgroundColor = "#229EE0";
        z[1].style.backgroundColor = "#229EE0";
        z[2].style.backgroundColor = "#229EE0";

    // hot pink to dark blue
    var q = document.querySelectorAll("#hotpink0, #hotpink1, #hotpink2");
        q[0].style.backgroundColor = "#1A6CA3";
        q[1].style.backgroundColor = "#1A6CA3";
        q[2].style.backgroundColor = "#1A6CA3";

     // pink to orange
    var w = document.querySelectorAll("#pink0, #pink1");
        w[0].style.backgroundColor = "#f8ae6b";
        w[1].style.backgroundColor = "#f8ae6b";

}